<?php

require_once dirname(__FILE__) . '/../../PhraseanetWebTestCaseAuthenticatedAbstract.class.inc';

use Symfony\Component\Console\Tester\CommandTester;
use \Symfony\Component\Console\Application;
/**
 * Test class for module_console_aboutLicense.
 * Generated by PHPUnit on 2011-11-03 at 16:10:59.
 */
class module_console_aboutLicenseTest extends PHPUnit_Framework_TestCase
{

  /**
   * @var module_console_aboutLicense
   */
  protected $object;

  /**
   * @covers module_console_aboutAuthors::execute
   */
  public function testExecute()
  {
    // mock the Kernel or create one depending on your needs
    $application = new Application();
    $application->add(new module_console_aboutLicense('about:license'));

    $command = $application->find('about:license');
    $commandTester = new CommandTester($command);
    $commandTester->execute(array('command' => $command->getName()));

    $this->assertEquals(
            trim(file_get_contents(dirname(__FILE__) . '/../../../../LICENSE'))
            , trim($commandTester->getDisplay())
    );
  }

}

?>
